mapper.insert:
insert into

(tool_code,tool_name,version

    , is_enable


    , logo


    , description


    , sort

    )
values
(#{toolCode}, #{toolName}, #{version}

    , #{isEnable}


    , #{logo}


    , #{description}


    , #{sort}
)

mapper.update:
update

set updated_at = now()

    , tool_code = #{toolCode}


    , tool_name = #{toolName}


    , version = #{version}


    , is_enable = #{isEnable}


    , logo = #{logo}


    , description = #{description}


    , sort = #{sort}

where id = #{id}

update 
set is_deleted = 1
where tool_code = #{toolCode} and version = #{version}
